{{(()=>{
    if($json?.stdout){
    const body = JSON.parse($json.stdout).res.body;

    if (body === "FLOW_FINISH") {
        return 'echo "FLOW_FINISH" >> /home/node/python/prompt_exec.txt';
    }
    body.replace("FLOW_FINISH", "")
    return body
        .replace(/\r?\n/g, '; ')   // elimina saltos de línea
        .replace(/\t/g, ' ')     // elimina tabs
        .trim();                 // elimina espacios al inicio/fin
} else {return $json.error}
})()}}
